package com.pcc.test;

import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.atomic.AtomicLong;

/**
 * @author peichenchen
 * @date 2019/03/29
 */
public class Test {

    public static void main(String[] args) {

        String str = "";
        Map<Character, AtomicLong> str2count = new ConcurrentHashMap<>();
        System.out.println(str2count);

        char[] chars = str.toCharArray();

        CountDownLatch countDownLatch = new CountDownLatch(10);

        for (int i = 0; i < 10; i++) {
            int start = i * 1000;
            int end = start + 999;
            Thread thread = new Thread(() -> {
                System.out.println("start=" + start + " end=" + end);
                for (int index = start; index <= end; index++) {
                    Character c = chars[index];
                    AtomicLong count = str2count.get(c);
                    if (count == null) {
                        count = new AtomicLong();
                        AtomicLong curCount = str2count.putIfAbsent(c, count);
                        if (curCount != null) {
                            count = curCount;
                        }
                    }
                    count.incrementAndGet();

                }
                countDownLatch.countDown();
            });
            thread.start();
        }


        try {
            countDownLatch.await();
        } catch (InterruptedException e) {
            e.printStackTrace();
        }
        System.out.println(str2count);


    }


}
